@import url("https://fonts.googleapis.com/css2?family=Poppins:wght@200;300;400;500;600;700&display=swap");
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  font-family: "Poppins", sans-serif;
}
body {
    margin: 0;
    padding: 0;
     min-height: 100vh;
    font-family: sans-serif;
    background: #35696b;
}
.navbar {
    border-bottom: 1px solid #e2e6e9;
    background: #325b75;
}
.navbar-light .navbar-brand {
    color: #FFEB3B;
}
.navbar-light .navbar-nav .nav-link {
    color: #ffffff;
    font-size: .875rem;
    font-weight: 500;
}
/* Pre css */
.flex {
  display: flex;
  align-items: center;
}

.sidebar {
    color:#fff;
  position: absolute;
  top: 73px;
  left: 0;
  height: 100%;
  width: 270px;
  background: #35696b;
  padding: 15px 10px;
  box-shadow: 0 0 2px rgba(0, 0, 0, 0.1);
  transition: all 0.4s ease;
}
.sidebar.close {
  width: calc(55px + 20px);
}
.logo_items {
  gap: 8px;
}
.logo_name {
  font-size: 22px;
  color: #333;
  font-weight: 500px;
  transition: all 0.3s ease;
}
.sidebar.close .logo_name,
.sidebar.close #lock-icon,
.sidebar.close #sidebar-close {
  opacity: 0;
  pointer-events: none;
}
#lock-icon,
#sidebar-close {
  padding: 10px;
  color: #4070f4;
  font-size: 25px;
  cursor: pointer;
  margin-left: -4px;
  transition: all 0.3s ease;
}
#sidebar-close {
  display: none;
  color: #333;
}
.menu_container {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  margin-top: 40px;
  overflow-y: auto;
  height: calc(100% - 82px);
}
.menu_container::-webkit-scrollbar {
  display: none;
}
.menu_title {
  position: relative;
  height: 50px;
  width: 55px;
}
.menu_title .title {
  margin-left: 15px;
  transition: all 0.3s ease;
}
.sidebar.close .title {
  opacity: 0;
}
.menu_title .line {
  position: absolute;
  left: 50%;
  transform: translateX(-50%);
  height: 3px;
  width: 20px;
  border-radius: 25px;
  background: #aaa;
  transition: all 0.3s ease;
}
.menu_title .line {
  opacity: 0;
}
.sidebar.close .line {
  opacity: 1;
}
.item {
  list-style: none;
}
.link {
  text-decoration: none;
  border-radius: 8px;
  margin-bottom: 8px;
  color: #707070;
}
.link:hover {
  color: #fff;
  background-color: #ffffff29;
}
.link span {
    color: #fff;
  white-space: nowrap;
}
.link i {
    color: #fff;
  height: 50px;
  min-width: 55px;
  display: flex;
  font-size: 22px;
  align-items: center;
  justify-content: center;
  border-radius: 4px;
}

.sidebar_profile {
  padding-top: 15px;
  margin-top: 15px;
  gap: 15px;
  border-top: 2px solid rgba(0, 0, 0, 0.1);
}
.sidebar_profile .name {
  font-size: 18px;
  color: #333;
}
.sidebar_profile .email {
  font-size: 15px;
  color: #333;
}


@media screen and (max-width: 800px) {
  .sidebar {
    left: 0;
    z-index: 1000;
  }
  .sidebar.close {
    left: -100%;
  }
  #sidebar-close {
    display: block;
  }
  #lock-icon {
    display: none;
  }
  .navbar {
    left: 0;
    max-width: 100%;
    transform: translateX(0%);
  }
  #sidebar-open {
    display: block;
  }
}
.sidebar-column{
    background: #ffffff;
    height: 100vh;
    background-color: #28283f;
    margin-left: -5%;
}
.web-sidebar {
     transform: translateX(100%);
    padding-left: 3rem;
    padding-top: 4rem;
    position: absolute;
    top: 0;
}


.sidebar-item a {
    display: block;
    padding: 0.25rem 0.5rem;
    margin-top: 0.25rem;
    border-radius: 0.375rem;
    font-size: .875rem;
    color: #ffffff;
    text-decoration: none;
    font-weight: 500;
    transition: all .2s ease-in-out;
}
/*.col-sm-2 {*/
/*    flex: 0 0 24.6666666667%;*/
/*    max-width: 24.6666666667%;*/
/*}*/

.h3 {
    margin-bottom: 0.5rem;
    font-size:10px;
    /*font-family: Montserrat,Georgia,'Times New Roman',Times,serif;*/
    font-weight: 400;
    line-height: 1.2;
    color: inherit;
}
.form-control-lg {
    height: calc(3.5625rem + 2px);
    padding: 1rem 1.25rem;
    font-size: 1.25rem;
    line-height: 1.25;
    border-radius: 0.27rem;
}
.table, th, td {
    padding:15px;
    text-align:left;
    height:50px;
  border:1px solid black;
}


.container-fluid {
    border: 1px solid #7b389d;
    margin-top: 0px;
    position: relative;
    max-width: 82%;
    width: 100%;
    border-radius: 6px;
    padding: 30px;
    margin-left: 18%;
    background-color: #fff;
    box-shadow: 0 5px 10px rgba(0,0,0,0.1);
}
}
.container-fluid header{
    position: relative;
    font-size: 20px;
    font-weight: 600;
    color: #333;
}
.container-fluid header::before{
    content: "";
    position: absolute;
    left: 0;
    bottom: -2px;
    height: 3px;
    width: 27px;
    border-radius: 8px;
    background-color: #4070f4;
}
.container-fluid form{
    position: relative;
    margin-top: 16px;
    min-height: 1150px;
    background-color: #fff;
    overflow: hidden;
}
.container-fluid form .form{
    position: absolute;
    background-color: #fff;
    transition: 0.3s ease;
}
.container-fluid form .form.second{
    opacity: 0;
    pointer-events: none;
    transform: translateX(100%);
}
form.secActive .form.second{
    opacity: 1;
    pointer-events: auto;
    transform: translateX(0);
}
form.secActive .form.first{
    opacity: 0;
    pointer-events: none;
    transform: translateX(-100%);
}
.container-fluid form .title{
    display: block;
    margin-bottom: 8px;
    font-size: 16px;
    font-weight: 500;
    margin: 6px 0;
    color: #333;
}
.container-fluid form .fields{
    display: flex;
    align-items: center;
    justify-content: space-between;
    flex-wrap: wrap;
}
form .fields .input-field{
    display: flex;
    width: calc(100% / 3 - 15px);
    flex-direction: column;
    margin: 4px 0;
}
.input-field label{
    font-size: 12px;
    font-weight: 500;
    color: #2e2e2e;
}
.input-field input, select{
    outline: none;
    font-size: 14px;
    font-weight: 400;
    color: #333;
    border-radius: 5px;
    border: 1px solid #aaa;
    padding: 0 15px;
    height: 42px;
    margin: 8px 0;
}
.input-field input :focus,
.input-field select:focus{
    box-shadow: 0 3px 6px rgba(0,0,0,0.13);
}
.input-field select,
.input-field input[type="date"]{
    color: #707070;
}
.input-field input[type="date"]:valid{
    color: #333;
}
.container-fluid form button, .backBtn{
    display: flex;
    align-items: center;
    justify-content: center;
    height: 45px;
    max-width: 200px;
    width: 100%;
    border: none;
    outline: none;
    color: #fff;
    border-radius: 5px;
    margin: 25px 0;
    background-color: #4070f4;
    transition: all 0.3s linear;
    cursor: pointer;
}
.container-fluid form .btnText{
    font-size: 14px;
    font-weight: 400;
}
form button:hover{
    background-color: #265df2;
}
form button i,
form .backBtn i{
    margin: 0 6px;
}
form .backBtn i{
    transform: rotate(180deg);
}
form .buttons{
    display: flex;
    align-items: center;
}
form .buttons button , .backBtn{
    margin-right: 14px;
}
@media (max-width: 750px) {
    .container-fluid form{
        overflow-y: scroll;
    }
    .container-fluid form::-webkit-scrollbar{
       display: none;
    }
    form .fields .input-field{
        width: calc(100% / 2 - 15px);
    }
}
@media (max-width: 550px) {
    form .fields .input-field{
        width: 100%;
    }
}

