    const canvas = document.getElementById('signatureCanvas');
    const ctx = canvas.getContext('2d');

    let isSigning = false;

    canvas.addEventListener('mousedown', startSignature);
    canvas.addEventListener('mousemove', drawSignature);
    canvas.addEventListener('mouseup', endSignature);
    canvas.addEventListener('mouseout', endSignature);

    // Load the saved signature if available
    window.addEventListener('load', loadSignature);

    function startSignature(e) {
        isSigning = true;
        drawSignature(e);
    }

    function drawSignature(e) {
        if (!isSigning) return;

        const rect = canvas.getBoundingClientRect();
        const x = e.clientX - rect.left;
        const y = e.clientY - rect.top;

        ctx.lineTo(x, y);
        ctx.stroke();
    }

    function endSignature() {
        isSigning = false;
        saveSignature();
    }

    function clearSignature() {
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        saveSignature(); // Save an empty signature to clear the stored signature data
    }

    function saveSignature() {
        const dataURL = canvas.toDataURL(); // Convert canvas content to a base64 data URL
        localStorage.setItem('signature', dataURL);
    }

    function loadSignature() {
        const savedSignature = localStorage.getItem('signature');
        if (savedSignature) {
            const img = new Image();
            img.src = savedSignature;
            img.onload = function() {
                ctx.drawImage(img, 0, 0); // Load the saved signature as an image on the canvas
            };
        }
    }

